import "core-js/modules/es.array.push.js";
export default {
  name: "login",
  data() {
    return {
      loginForm: {
        username: "",
        password: ""
      },
      loading: false,
      pwdType: "password"
    };
  },
  methods: {
    showPwd() {
      if (this.pwdType === "password") {
        this.pwdType = "";
      } else {
        this.pwdType = "password";
      }
    },
    handleLogin() {
      this.$refs.loginForm.validate(valid => {
        if (valid) {
          this.loading = true;
          this.$store.dispatch("Login", this.loginForm).then(response => {
            this.loading = false;
            let code = response.data.code;
            if (code == 200) {
              this.$router.push({
                path: "/success",
                query: {
                  data: response.data.data
                }
              });
            } else {
              this.$router.push({
                path: "/error",
                query: {
                  message: response.data.message
                }
              });
            }
          }).catch(() => {
            this.loading = false;
          });
        } else {
          console.log("参数验证不合法！");
          return false;
        }
      });
    }
  }
};
